import React from 'react';
import { useSelector } from 'react-redux';
import { useHistory } from 'react-router-dom';
import { makeStyles } from '@material-ui/core/styles';
import { Grid, Typography, Button, TextField, MenuItem } from '@material-ui/core';

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
    padding: theme.spacing(3),
  },
  formControl: {
    margin: theme.spacing(1),
    minWidth: 120,
  },
  selectEmpty: {
    marginTop: theme.spacing(2),
  },
  button: {
    marginTop: theme.spacing(2),
  },
}));

const FlightBooking = () => {
  const classes = useStyles();
  const history = useHistory();
  const bookingData = useSelector((state) => state.flightSearch.bookingDetails);
  // Example state, replace with your own state logic
  const [from, setFrom] = React.useState('');
  const [to, setTo] = React.useState('');
  const [date, setDate] = React.useState('');

  const handleFromChange = (event) => {
    setFrom(event.target.value);
  };

  const handleToChange = (event) => {
    setTo(event.target.value);
  };

  const handleDateChange = (event) => {
    setDate(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    // Implement your booking submission logic here
    console.log(from, to, date);
    history.push('/confirmation'); // Redirect to confirmation page or handle next steps
  };

  return (
    <div className={classes.root}>
      <Typography variant="h4" gutterBottom>
        Book Your Flight
      </Typography>
      <form onSubmit={handleSubmit}>
        <Grid container spacing={3}>
          <Grid item xs={12} sm={6}>
            <TextField
              required
              id="from"
              name="from"
              label="From"
              fullWidth
              autoComplete="from"
              value={from}
              onChange={handleFromChange}
              variant="outlined"
            />
          </Grid>
          <Grid item xs={12} sm={6}>
            <TextField
              required
              id="to"
              name="to"
              label="To"
              fullWidth
              autoComplete="to"
              value={to}
              onChange={handleToChange}
              variant="outlined"
            />
          </Grid>
          <Grid item xs={12}>
            <TextField
              id="date"
              label="Departure Date"
              type="date"
              defaultValue={date}
              onChange={handleDateChange}
              className={classes.textField}
              InputLabelProps={{
                shrink: true,
              }}
              fullWidth
              variant="outlined"
            />
          </Grid>
          <Grid item xs={12}>
            <Button type="submit" variant="contained" color="primary" className={classes.button}>
              Book Flight
            </Button>
          </Grid>
        </Grid>
      </form>
    </div>
  );
};

export default FlightBooking;
