import React from 'react';
import { useSelector } from 'react-redux';
import { useHistory } from 'react-router-dom';
import { makeStyles } from '@material-ui/core/styles';
import { Paper, Grid, Typography, Button, TextField, FormControl, InputLabel, Select, MenuItem } from '@material-ui/core';

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
    padding: theme.spacing(5),
    backgroundColor: '#e3f2fd', // Light blue background
  },
  paper: {
    padding: theme.spacing(4),
    textAlign: 'center',
    color: theme.palette.text.secondary,
    marginBottom: theme.spacing(4),
  },
  formControl: {
    margin: theme.spacing(1),
    minWidth: 120,
    width: '100%',
  },
  selectEmpty: {
    marginTop: theme.spacing(2),
  },
  submitButton: {
    marginTop: theme.spacing(4),
    backgroundColor: '#1e88e5', // Blue
    color: '#fff',
    '&:hover': {
      backgroundColor: '#1565c0',
    },
  },
}));

const FlightBooking = () => {
  const classes = useStyles();
  const history = useHistory();
  const bookingData = useSelector((state) => state.flightSearch.bookingDetails);
  const [from, setFrom] = React.useState('');
  const [to, setTo] = React.useState('');
  const [date, setDate] = React.useState('');

  const handleFromChange = (event) => {
    setFrom(event.target.value);
  };

  const handleToChange = (event) => {
    setTo(event.target.value);
  };

  const handleDateChange = (event) => {
    setDate(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    console.log(from, to, date);
    history.push('/confirmation');
  };

  return (
    <div className={classes.root}>
      <Paper className={classes.paper}>
        <Typography variant="h5" gutterBottom>
          Book Your Flight Now
        </Typography>
        <form onSubmit={handleSubmit} noValidate>
          <Grid container spacing={3}>
            <Grid item xs={12} sm={6}>
              <TextField
                required
                id="from"
                name="from"
                label="From"
                fullWidth
                autoComplete="from"
                value={from}
                onChange={handleFromChange}
                variant="outlined"
              />
            </Grid>
            <Grid item xs={12} sm={6}>
              <TextField
                required
                id="to"
                name="to"
                label="To"
                fullWidth
                autoComplete="to"
                value={to}
                onChange={handleToChange}
                variant="outlined"
              />
            </Grid>
            <Grid item xs={12}>
              <TextField
                id="date"
                label="Departure Date"
                type="date"
                defaultValue={date}
                onChange={handleDateChange}
                className={classes.textField}
                InputLabelProps={{
                  shrink: true,
                }}
                fullWidth
                variant="outlined"
              />
            </Grid>
            <Grid item xs={12}>
              <Button type="submit" variant="contained" className={classes.submitButton}>
                Book Flight
              </Button>
            </Grid>
          </Grid>
        </form>
      </Paper>
    </div>
  );
};

export default FlightBooking;
