import React, { useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import { Paper, Grid, TextField, Button, IconButton } from '@material-ui/core';
import SwapHorizIcon from '@material-ui/icons/SwapHoriz';

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
    padding: theme.spacing(2),
  },
  paper: {
    padding: theme.spacing(2),
    textAlign: 'center',
    color: theme.palette.text.secondary,
  },
  swapButton: {
    margin: theme.spacing(1),
  },
  findButton: {
    backgroundColor: '#4caf50', // Button color from the screenshot
    color: '#fff',
    '&:hover': {
      backgroundColor: '#388e3c',
    },
  },
}));

const FlightBooking = () => {
  const classes = useStyles();
  const [from, setFrom] = useState('');
  const [to, setTo] = useState('');
  const [departDate, setDepartDate] = useState('');
  const [returnDate, setReturnDate] = useState('');

  const swapLocations = () => {
    let temp = from;
    setFrom(to);
    setTo(temp);
  };

  return (
    <div className={classes.root}>
      <Paper elevation={3}>
        <Grid container spacing={2} alignItems="center" justify="center">
          <Grid item xs={12} sm={3}>
            <TextField
              id="from"
              label="From"
              variant="outlined"
              fullWidth
              value={from}
              onChange={(e) => setFrom(e.target.value)}
            />
          </Grid>
          <Grid item>
            <IconButton color="default" className={classes.swapButton} onClick={swapLocations}>
              <SwapHorizIcon />
            </IconButton>
          </Grid>
          <Grid item xs={12} sm={3}>
            <TextField
              id="to"
              label="To"
              variant="outlined"
              fullWidth
              value={to}
              onChange={(e) => setTo(e.target.value)}
            />
          </Grid>
          <Grid item xs={12} sm={3}>
            <TextField
              id="depart-date"
              label="Depart Date"
              type="date"
              variant="outlined"
              fullWidth
              value={departDate}
              onChange={(e) => setDepartDate(e.target.value)}
              InputLabelProps={{
                shrink: true,
              }}
            />
          </Grid>
          <Grid item xs={12} sm={3}>
            <TextField
              id="return-date"
              label="Return Date"
              type="date"
              variant="outlined"
              fullWidth
              value={returnDate}
              onChange={(e) => setReturnDate(e.target.value)}
              InputLabelProps={{
                shrink: true,
              }}
            />
          </Grid>
          <Grid item xs={12}>
            <Button variant="contained" className={classes.findButton}>
              Find Trains
            </Button>
          </Grid>
        </Grid>
      </Paper>
    </div>
  );
};

export default FlightBooking;
